-(void)ScollViewDidSelect:(int)SelectedIndex
{
    //根据不同的编号做不同的菜
    switch (SelectedIndex) {
        case 1:
        {
            NSLog(@"slel %d",SelectedIndex);
            UIImageView* tmp=[[UIImageView alloc] initWithFrame:CGRectMake(30, 30, 150, 150)];
            tmp.image=[UIImage imageNamed:@"Icon.png"];
            [self.view addSubview:tmp];
            break;
        }

        default:
            break;
    }
}

