DROP PROCEDURE IF EXISTS buy_product;
delimiter |
create procedure buy_product(in_account varchar(50),in_productId int,in_price int,out out_rs varchar(50))
begin
  declare countStock int default -1;
  declare tmpNo varchar(50) default '';
  select count(*) into countStock from product_detail where product_id=in_productId;
  set out_rs='失败';
  if countStock>0 then
     start transaction;
        select product_no into tmpNo from product_detail where product_id=in_productId limit 0,1;
        insert into buy_his values(in_account,in_productId,tmpNo,now(),in_price);
        delete from product_detail where product_id=in_productID and product_no=tmpNo;
     commit;
     set out_rs='购买成功';
  else
     set out_rs='库存不足';
  end if;
end;
|
delimiter ;

create procedure sp_find(pfind varchar(500) 
BEGIN 
DECLAR msql varchar(2000); 
SET @MyQuery=Concat('select * from 表 where ',pfind); 
PREPARE msql from @MyQuery; 
EXECUTE msql; 
END

