var aa:GameObject;//A
var bb:GameObject;//B
var dis: float;
function Update () {
  dis=Vector3.Distance(bb.transform.position,aa.transform.position);
}

function OnGUI () {
  GUI.Label (Rect (10,40,200,20), dis + "m");
}

var startPoint:Vector3;
var endPoint:Vector3;
var showDistance:float = 0.0;
var isStart:boolean;
var isOver:boolean;
var isFollow:boolean;
var mLine:LineRenderer;
var aMaterial:Material;
var mx:float = 0.0;
var my:float = 0.0;

function Start ()
{
   mLine = this.gameObject.AddComponent(LineRenderer);
   mLine.SetWidth(1, 1);
   mLine.SetVertexCount(2);
   mLine.SetColors (Color.yellow,Color.yellow);
   //aMaterial.color = Color.red;
   mLine.material = aMaterial;
   mLine.material.color = Color (0, 1, 0, 0.25);
   mLine.renderer.enabled = true;
}

function Update () {
//print(Input.mousePosition);
if (Input.GetButtonDown ("Fire1")) {
   showDistance = 0;
   startPoint = Vector3.zero;
   endPoint = Vector3.zero;
   isFollow = true;
   var sRay = camera.ScreenPointToRay (Input.mousePosition);
   var sHit : RaycastHit;
   if (Physics.Raycast (sRay,sHit)) {
    //Debug.DrawRay (ray.origin, ray.direction * 10, Color.yellow);
    //Debug.DrawLine (sRay.origin, sHit.point);
    startPoint = sHit.point;
    print("startPoint:" +sHit.point);
    mLine.SetPosition(0,startPoint);
    isStart = true;
    isOver = false;
   }else{
    print("");
   }
}

if(isStart && !isOver){
   var mRay = camera.ScreenPointToRay (Input.mousePosition);
   var mHit : RaycastHit;
   if (Physics.Raycast (mRay,mHit)) {
    endPoint = mHit.point;
    print("mPoint:" +mHit.point);
    mLine.SetPosition(1,endPoint);
    showDistance = Vector3.Distance(endPoint,startPoint);
   }else{
    print("");
   }
}

if (Input.GetButtonUp ("Fire1")) {
   var eRay = camera.ScreenPointToRay (Input.mousePosition);
   var eHit : RaycastHit;
   isFollow = false;
   if (Physics.Raycast(eRay,eHit)) {
    //Debug.DrawRay (ray.origin, ray.direction * 10, Color.yellow);
    //Debug.DrawLine (eRay.origin, eHit.point);
    endPoint = eHit.point;
    print("endPoint:" +eHit.point);
    isOver = true;
    isStart = false;
    showDistance = Vector3.Distance(endPoint,startPoint);
    //Debug.DrawLine (startPoint,endPoint, Color.red);
    mLine.SetPosition(1,endPoint);
   }else{
    print("");
   }
}
}




function OnGUI () {
//GUI.TextArea (Rect (300, 80, 50, 20), showDistance.ToString());
if(isFollow){
   mx = Input.mousePosition.x + 5;
   my = Screen.height-Input.mousePosition.y - 20;
}
GUI.Label(Rect (mx,my, 100, 20), showDistance.ToString() + " M");
}

