var b:GameObject;  
//a,b 分别定义两个公共GameObject对象//  
function Update () {  
if (a==null || b==null) {  
print("a or b = null");  
return;  
//如果a或者是b实例化失败就跳出函数  
}  
var m:Vector3;  
var n:Vector3;  
//m,n定义两个私有 Vector3类型  
m=a.transform.position;  
n=b.transform.position;  
//赋m,n予a,b的位置  
print(Vector3.Distance(m,n));  
//函数Vector3.Distance计算a，b间距，并在控制台输出  
}
